import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';

// Import auth components
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import ConfirmOTP from './components/auth/ConfirmOTP';
import ForgotPassword from './components/auth/ForgotPassword';
import ResetOTP from './components/auth/ResetOTP';
import ResetPassword from './components/auth/ResetPassword';
import VerifyNewEmail from './components/auth/VerifyNewEmail';

// Import common components
import Navbar from './components/common/Navbar';
import PrivateRoute from './components/common/PrivateRoute';

function App() {
  return (
    <Router>
      <div className="App">
        <Routes>
          {/* Public Routes - Auth Pages (No Navbar) */}
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/confirm-otp" element={<ConfirmOTP />} />
          <Route path="/forgot-password" element={<ForgotPassword />} />
          <Route path="/reset-otp" element={<ResetOTP />} />
          <Route path="/reset-password" element={<ResetPassword />} />

          {/* Protected Routes - Require Authentication */}
          <Route path="/verify-new-email" element={
            <PrivateRoute>
              <VerifyNewEmail />
            </PrivateRoute>
          } />

          {/* Public Routes with Navbar */}
          <Route path="/" element={
            <>
              <Navbar />
              <div className="container mt-4">
                <h1>Welcome to Anvi Studio Boutique</h1>
                <p>Your destination for exquisite traditional and contemporary ethnic wear.</p>
              </div>
            </>
          } />

          {/* Catch all - redirect to home */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
